import React, { Component } from 'react';

class AddAFriend extends Component {

    constructor(props){
        super(props);
        this.state = {
            Name:'',
            MobileNumber:'',
            FavouriteGame:'',
            DOB:''
        }
    }

    onChangeEvent = (e) => {
       e.preventDefault();
       this.setState ({
        [e.target.name]: e.target.value
       })
    }

    onHandleSubmit = (e) => {
        e.preventDefault();
        const friendData = {
            Name:this.state.Name,
            Mobile:this.state.MobileNumber,
            DOB:this.state.DOB,
            FavouriteGame:this.state.FavouriteGame
        }

        console.log(friendData)
    }

    render() {
        return (
        <div className='container'>
            <form>
           <div className="row">
            <div className="col-6">
                name : <input name='Name' placeholder='enter-name'></input>
            </div>
            <div className="col-6">
                Mobile Number : <input name='MobileNumber' placeholder='Mobile-Number'></input>
            </div>
            </div>

            <div className="row">
            <div className="col-6">
                Birthday : <input name='DOB' placeholder='Birthday'></input>
            </div>
            <div className="col-6">
                Favourite game : <input name='FavouriteGame' placeholder='favourite game'></input>
            </div>
            </div>
            </form>

        </div>
        );
    }
}

export default AddAFriend;
