import React, { Component } from 'react';

class AddAFriend extends Component {

    constructor(props){
        super(props);
        this.state = {
            Name:'',
            MobileNumber:'',
            FavouriteGame:'',
            DOB:''
        }
    }

    onChangeEvent = (e) => {
       e.preventDefault();
       this.setState ({
        [e.target.name]: e.target.value
       })
    }

    render() {
        return (
        <div className='container'>

           <div className="row">
            <div className="col-6">
                name : <input placeholder='enter-name'></input>
            </div>
            <div className="col-6">
                Mobile Number : <input placeholder='Mobile-Number'></input>
            </div>
            </div>

            <div className="row">
            <div className="col-6">
                Birthday : <input placeholder='Birthday'></input>
            </div>
            <div className="col-6">
                Favourite game : <input placeholder='favourite game'></input>
            </div>
            </div>

        </div>
        );
    }
}

export default AddAFriend;
