import axios from "axios"

export const friendAdd = (friendData, edit=false) =>  async dispatch => {
    try {
        const headers = {AuthenticationToken:localStorage.getItem('token')}
        const res =  axios.post('/user/addfriend',friendData,{headers:headers})
        // console.log(res)
       if(!edit){
           history.pushState('/friendlist')
       }
    } catch (error) {
        console.log(error);
    }
}
