import axios from 'axios';
import {LOGIN_SUCCSESS} from '../types'

export const userLogin = (loginData) => async dispatch => {

   try {
       const res = await axios.post('user/login',loginData);

       dispatch({
           type:LOGIN_SUCCSESS,
           payload:res.data
       })
   } catch (error) {
       console.log(error);
   } 

}
