import axios from "axios";

import { FRIEND_EDIT } from "../types";

export const EditTheFriend = (friendID,formData, history) => dispatch => {
  console.log(formData);
  axios
    .patch('/user/myfriends/updatefriend/', formData)
    // .patch("/all_vendor/:vendorId", editUser)
    .then(res => history.push("/vendor-list"))
    .catch(err =>
      dispatch({
        type: FRIEND_EDIT,
        payload: err.response.data
      })
    );
};
